import React, { useState, useEffect } from 'react';
import AgregarProveedor from './AgregarProveedor';

const Proveedores = () => {
  const [proveedores, setProveedores] = useState([]);

  useEffect(() => {
    // Hacer la solicitud a la API
    fetch('http://localhost:3003/api/proveedores')
      .then((response) => response.json())
      .then((data) => setProveedores(data))  // Establecer los proveedores en el estado
      .catch((error) => console.error('Error al obtener proveedores:', error));
  }, []);  // El array vacío asegura que se ejecute una vez al montar el componente

  return (
    <div>
      <h1>Gestión de Proveedores</h1>

      {/* Mostrar la lista de proveedores */}
      <h2>Lista de Proveedores</h2>
      {proveedores.length > 0 ? (
        <div className="row">
          {proveedores.map((proveedor) => (
            <div key={proveedor._id} className="col-md-4 mb-4">
              <div className="card shadow-sm">
                <div className="card-body">
                  <h5 className="card-title">{proveedor.nombre}</h5>
                  <p className="card-text"><strong>Dirección:</strong> {proveedor.direccion}</p>
                  <p className="card-text"><strong>Teléfono:</strong> {proveedor.telefono}</p>
                  <p className="card-text"><strong>Email:</strong> {proveedor.email}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No hay proveedores disponibles.</p>
      )}

      {/* Botones de editar y eliminar */}
      <button>Editar</button>
      <button>Eliminar</button>

      {/* Componente para agregar un nuevo proveedor */}
      <AgregarProveedor />
    </div>
  );
};

export default Proveedores;
