import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Proveedores = () => {
  const [proveedores, setProveedores] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:3003/api/proveedores')
      .then((response) => {
        setProveedores(response.data);  // Guardamos los datos en el estado
        setLoading(false);  // Desactivamos el estado de carga
      })
      .catch((error) => {
        console.error('Error al obtener proveedores:', error);
        setLoading(false);  // Detenemos la carga si ocurre un error
      });
  }, []);  // Solo se ejecuta cuando el componente se monta

  if (loading) {
    return <p>Cargando proveedores...</p>;
  }

  return (
    <div>
      <h2 className="mb-4 text-center">Lista de Proveedores</h2>
      {proveedores.length > 0 ? (
        <div className="row">
          {proveedores.map((proveedor) => (
            <div className="col-md-4 mb-4" key={proveedor._id}>
              <div className="card shadow-sm">
                <div className="card-body">
                  <h5 className="card-title">{proveedor.nombre}</h5>
                  <p className="card-text"><strong>Dirección:</strong> {proveedor.direccion}</p>
                  <p className="card-text"><strong>Teléfono:</strong> {proveedor.telefono}</p>
                  <p className="card-text"><strong>Email:</strong> {proveedor.email}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No hay proveedores disponibles.</p>
      )}
      <button className="btn btn-primary mt-3">Agregar Proveedor</button>
    </div>
  );
};

export default Proveedores;
