import React, { useState, useEffect } from 'react';

const Productos = () => {
  const [productos, setProductos] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(false); // Controla si el formulario está visible
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [precio, setPrecio] = useState('');
  const [proveedor, setProveedor] = useState('');
  const [proveedores, setProveedores] = useState([]);

  useEffect(() => {
    // Obtener los productos
    fetch('http://localhost:3001/api/productos')
      .then((response) => response.json())
      .then((data) => setProductos(data))
      .catch((error) => console.error('Error al obtener productos:', error));

    // Obtener los proveedores para el select
    fetch('http://localhost:3003/api/proveedores')
      .then((response) => response.json())
      .then((data) => setProveedores(data))
      .catch((error) => console.error('Error al obtener proveedores:', error));
  }, []);

  const eliminarProducto = (id) => {
    fetch(`http://localhost:3001/api/productos/${id}`, {
      method: 'DELETE',
    })
      .then((response) => {
        if (response.ok) {
          setProductos((prev) => prev.filter((producto) => producto._id !== id));
        } else {
          console.error('Error al eliminar producto');
        }
      })
      .catch((error) => console.error('Error al eliminar producto:', error));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Llamada a la API para agregar el producto
    fetch('http://localhost:3001/api/productos/agregar', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        nombre,
        descripcion,
        precio,
        proveedor,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.mensaje === 'Producto agregado exitosamente') {
          alert('Producto agregado exitosamente');
          setNombre('');
          setDescripcion('');
          setPrecio('');
          setProveedor('');
          setMostrarFormulario(false); // Ocultar formulario después de agregar
          // Actualizar la lista de productos sin recargar
          setProductos([...productos, { nombre, descripcion, precio, proveedor }]);
        } else {
          alert('Error al agregar el producto');
        }
      })
      .catch((error) => {
        console.error('Error al agregar el producto:', error);
        alert('Error al agregar el producto');
      });
  };

  return (
    <div className="container my-5">
      <h1 className="text-center mb-4">Gestión de Productos</h1>

      {/* Botón para mostrar/ocultar el formulario de agregar producto */}
      <div className="text-center mt-4">
        <button
          className="btn btn-primary"
          onClick={() => setMostrarFormulario(!mostrarFormulario)}
        >
          {mostrarFormulario ? 'Cancelar' : 'Agregar Producto'}
        </button>
      </div>

      {/* Mostrar formulario de agregar producto */}
      {mostrarFormulario && (
        <form onSubmit={handleSubmit} className="form-group mt-4 shadow-lg p-4 rounded bg-light">
          <div className="mb-3">
            <label htmlFor="nombre" className="form-label">Nombre del Producto</label>
            <input
              type="text"
              id="nombre"
              className="form-control"
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="descripcion" className="form-label">Descripción</label>
            <input
              type="text"
              id="descripcion"
              className="form-control"
              value={descripcion}
              onChange={(e) => setDescripcion(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="precio" className="form-label">Precio</label>
            <input
              type="number"
              id="precio"
              className="form-control"
              value={precio}
              onChange={(e) => setPrecio(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="proveedor" className="form-label">Proveedor</label>
            <select
              id="proveedor"
              className="form-control"
              value={proveedor}
              onChange={(e) => setProveedor(e.target.value)}
              required
            >
              <option value="">Selecciona un proveedor</option>
              {proveedores.map((prov) => (
                <option key={prov._id} value={prov.nombre}>
                  {prov.nombre}
                </option>
              ))}
            </select>
          </div>
          <button type="submit" className="btn btn-primary">Agregar Producto</button>
        </form>
      )}

      <h2 className="mb-4">Lista de Productos</h2>
      {productos.length > 0 ? (
        <div className="row">
          {productos.map((producto) => (
            <div key={producto._id} className="col-md-4 mb-4">
              <div className="card shadow-lg border-light rounded">
                <div className="card-body">
                  <h5 className="card-title text-primary">{producto.nombre}</h5>
                  <p className="card-text"><strong>Descripción:</strong> {producto.descripcion}</p>
                  <p className="card-text"><strong>Precio:</strong> {producto.precio}</p>
                  <p className="card-text"><strong>Proveedor:</strong> {producto.proveedor}</p>
                  <button
                    className="btn btn-danger btn-sm mx-1"
                    onClick={() => eliminarProducto(producto._id)}
                  >
                    Eliminar
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center">No hay productos disponibles.</p>
      )}
    </div>
  );
};

export default Productos;
