import React, { useState, useEffect } from 'react';
import Suscribir from './Suscribir';

const Suscripciones = () => {
  const [fecha, setFecha] = useState('');
  const [empleado, setEmpleado] = useState('');
  const [cliente, setCliente] = useState('');  // Estado para el cliente seleccionado
  const [clientes, setClientes] = useState([]);
  const [empleados, setEmpleados] = useState([]);

  useEffect(() => {
    // Obtener la lista de empleados desde la API
    fetch('http://localhost:3005/api/empleados')
      .then((response) => response.json())
      .then((data) => setEmpleados(data))
      .catch((error) => console.error('Error al obtener empleados:', error));

    // Obtener la lista de clientes para mostrar en el formulario
    fetch('http://localhost:3006/api/clientes')
      .then((response) => response.json())
      .then((data) => setClientes(data))
      .catch((error) => console.error('Error al obtener clientes:', error));
  }, []);

  return (
    <div className="container my-5">
      <h2>Suscribir Cliente</h2>

      <form>
        <div className="mb-3">
          <label htmlFor="fecha" className="form-label">Fecha</label>
          <input
            type="date"
            id="fecha"
            value={fecha}
            onChange={(e) => setFecha(e.target.value)}
            className="form-control"
            required
          />
        </div>

        <div className="mb-3">
          <label htmlFor="empleado" className="form-label">Empleado</label>
          <select
            id="empleado"
            value={empleado}
            onChange={(e) => setEmpleado(e.target.value)}
            className="form-control"
            required
          >
            <option value="">Selecciona un empleado</option>
            {empleados.map((empleado) => (
              <option key={empleado._id} value={empleado._id}>
                {empleado.nombre}
              </option>
            ))}
          </select>
        </div>

        <div className="mb-3">
          <label htmlFor="cliente" className="form-label">Cliente</label>
          <select
            id="cliente"
            value={cliente}
            onChange={(e) => setCliente(e.target.value)}  // Establecer cliente seleccionado
            className="form-control"
            required
          >
            <option value="">Selecciona un cliente</option>
            {clientes.map((cliente) => (
              <option key={cliente._id} value={cliente._id}>
                {cliente.nombre} {cliente.apellido}
              </option>
            ))}
          </select>
        </div>

        {/* Bot√≥n para enviar datos al componente Suscribir */}
        {empleado && cliente && fecha && (
          <Suscribir fecha={fecha} empleado={empleado} cliente={cliente} />
        )}
      </form>
    </div>
  );
};

export default Suscripciones;
