// components/Proveedores.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Proveedores = () => {
  const [proveedores, setProveedores] = useState([]);

  useEffect(() => {
    // Obtener la lista de proveedores desde el backend
    const fetchProveedores = async () => {
      try {
        const response = await axios.get('http://localhost:3003/api/proveedores');

        // Log para inspeccionar la respuesta
        console.log('Respuesta del servidor:', response.data);

        // Asegurarse de que los datos sean un arreglo
        if (Array.isArray(response.data)) {
          setProveedores(response.data);
        } else {
          console.error('La respuesta no es un arreglo', response.data);
        }
      } catch (error) {
        console.error('Error al obtener los proveedores', error);
      }
    };

    fetchProveedores();
  }, []);

  // Función para eliminar un proveedor
  const eliminarProveedor = async (id) => {
    try {
      await axios.delete(`http://localhost:3003/api/proveedores/${id}`);
      setProveedores(proveedores.filter((proveedor) => proveedor._id !== id));  // Eliminar de la lista local
    } catch (error) {
      console.error('Error al eliminar el proveedor', error);
    }
  };

  return (
    <div className="container">
      <h2 className="mb-4 text-center">Lista de Proveedores</h2>

      {proveedores.length > 0 ? (
        <div className="row">
          {proveedores.map((proveedor) => (
            <div key={proveedor._id} className="col-md-4 mb-4">
              <div className="card shadow-sm">
                <div className="card-body">
                  <h5 className="card-title">{proveedor.nombre}</h5>
                  <p className="card-text"><strong>Dirección:</strong> {proveedor.direccion}</p>
                  <p className="card-text"><strong>Teléfono:</strong> {proveedor.telefono}</p>
                  <p className="card-text"><strong>Email:</strong> {proveedor.email}</p>
                  <button className="btn btn-primary" onClick={() => alert('Función de editar aún no implementada')}>Editar</button>
                  <button className="btn btn-danger" onClick={() => eliminarProveedor(proveedor._id)}>Eliminar</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No hay proveedores disponibles.</p>
      )}

      <div className="text-center mt-4">
        <button className="btn btn-success" onClick={() => window.location.href = "/agregar-proveedor"}>Agregar Proveedor</button>
      </div>
    </div>
  );
};

export default Proveedores;
