import React, { useState, useEffect } from 'react';

function ComprarProducto() {
  const [productos, setProductos] = useState([]);

  // Simulación de obtener productos desde la API
  useEffect(() => {
    // Aquí iría la lógica para obtener productos de la API
    fetch('http://localhost:3001/api/productos/lista')  // Cambia la URL si es necesario
      .then(response => response.json())
      .then(data => setProductos(data))
      .catch(error => console.error('Error al obtener productos:', error));
  }, []);

  return (
    <div className="container">
      <h1>Compra tu Producto</h1>

      <div className="row">
        {productos.length > 0 ? (
          productos.map((producto) => (
            <div key={producto.id} className="col-md-4">
              <div className="card">
                <img src={producto.image} alt={producto.nombre} className="card-img-top" />
                <div className="card-body">
                  <h5 className="card-title">{producto.nombre}</h5>
                  <p className="card-text">{producto.descripcion}</p>
                  <p className="card-text"><strong>Precio: ${producto.precio}</strong></p>
                  {/* Aquí puedes agregar un botón para agregar al carrito o comprar */}
                  <button className="btn btn-primary">Comprar</button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>No hay productos disponibles.</p>
        )}
      </div>
    </div>
  );
}

export default ComprarProducto;
