import React, { useState, useEffect } from "react";
import axios from "axios";

function ComprarProducto() {
  const [productos, setProductos] = useState([]);
  const [proveedores, setProveedores] = useState([]);
  const [selectedProducto, setSelectedProducto] = useState("");
  const [selectedProveedor, setSelectedProveedor] = useState("");

  useEffect(() => {
    // Obtener productos y proveedores
    axios.get("http://localhost:3001/api/productos/lista")
      .then((res) => setProductos(res.data));

    axios.get("http://localhost:3001/api/proveedores/lista")
      .then((res) => setProveedores(res.data));
  }, []);

  const handleCompra = () => {
    // Aquí va la lógica para hacer la compra (puedes enviar a tu backend)
    console.log(`Compra de producto: ${selectedProducto} de proveedor: ${selectedProveedor}`);
  };

  return (
    <div className="container p-5">
      <h2>Comprar Producto</h2>
      <div>
        <label>Producto:</label>
        <select onChange={(e) => setSelectedProducto(e.target.value)} className="form-control">
          <option value="">Seleccionar Producto</option>
          {productos.map((producto) => (
            <option key={producto._id} value={producto._id}>
              {producto.nombre}
            </option>
          ))}
        </select>
      </div>

      <div>
        <label>Proveedor:</label>
        <select onChange={(e) => setSelectedProveedor(e.target.value)} className="form-control">
          <option value="">Seleccionar Proveedor</option>
          {proveedores.map((proveedor) => (
            <option key={proveedor._id} value={proveedor._id}>
              {proveedor.nombre}
            </option>
          ))}
        </select>
      </div>

      <button onClick={handleCompra} className="btn btn-success mt-3">
        Comprar
      </button>
    </div>
  );
}

export default ComprarProducto;
