import React, { useState } from 'react';

const AgregarProveedor = () => {
  const [nombre, setNombre] = useState('');
  const [direccion, setDireccion] = useState('');
  const [telefono, setTelefono] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Llamada a la API para agregar el proveedor
    fetch('http://localhost:3001/api/proveedores/agregar', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        nombre,
        direccion,
        telefono,
        email,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.mensaje === 'Proveedor agregado exitosamente') {
          alert('Proveedor agregado exitosamente');
          setNombre('');
          setDireccion('');
          setTelefono('');
          setEmail('');
        } else {
          alert('Error al agregar el proveedor');
        }
      })
      .catch((error) => {
        console.error('Error al agregar el proveedor:', error);
        alert('Error al agregar el proveedor');
      });
  };

  return (
    <div className="container my-5">
      <h1 className="text-center mb-4">Agregar Proveedor</h1>
      <form onSubmit={handleSubmit} className="form-group">
        <div className="mb-3">
          <label htmlFor="nombre" className="form-label">Nombre del Proveedor</label>
          <input
            type="text"
            id="nombre"
            className="form-control"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="direccion" className="form-label">Dirección</label>
          <input
            type="text"
            id="direccion"
            className="form-control"
            value={direccion}
            onChange={(e) => setDireccion(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="telefono" className="form-label">Teléfono</label>
          <input
            type="text"
            id="telefono"
            className="form-control"
            value={telefono}
            onChange={(e) => setTelefono(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="email" className="form-label">Email</label>
          <input
            type="email"
            id="email"
            className="form-control"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Agregar Proveedor</button>
      </form>
    </div>
  );
};

export default AgregarProveedor;
