import React, { useState } from 'react';

const Suscribir = ({ fecha, empleado, cliente }) => {
  const [mensaje, setMensaje] = useState('');

  const handleSuscripcion = async (e) => {
    e.preventDefault();

    const datosSuscripcion = {
      fecha,
      empleado,
      cliente,
    };

    try {
      const response = await fetch('http://localhost:3006/api/clientes/suscribir', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(datosSuscripcion),
      });

      const data = await response.json();
      if (response.ok) {
        setMensaje('Cliente suscrito exitosamente');
      } else {
        setMensaje(`Error: ${data.mensaje}`);
      }
    } catch (error) {
      setMensaje('Error al suscribir al cliente');
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <button onClick={handleSuscripcion} className="btn btn-primary">Suscribir Cliente</button>
      {mensaje && <p className="mt-2">{mensaje}</p>}
    </div>
  );
};

export default Suscribir;
