import React, { useState, useEffect } from 'react';
import AgregarCliente from './AgregarCliente'; // Asegúrate de tener el componente AgregarCliente

const Clientes = () => {
  const [clientes, setClientes] = useState([]);

  // Obtener clientes al cargar el componente
  useEffect(() => {
    // Obtener clientes desde la API
    fetch('http://localhost:3006/api/clientes')  // Ruta para obtener clientes
      .then((response) => response.json())
      .then((data) => setClientes(data))
      .catch((error) => console.error('Error al obtener clientes:', error));
  }, []);

  const eliminarCliente = (id) => {
    // Eliminar cliente mediante el ID
    fetch(`http://localhost:3006/api/clientes/${id}`, { // Ruta para eliminar cliente
      method: 'DELETE',
    })
      .then((response) => {
        if (response.ok) {
          setClientes((prev) => prev.filter((cliente) => cliente._id !== id));
        } else {
          console.error('Error al eliminar cliente');
        }
      })
      .catch((error) => console.error('Error al eliminar cliente:', error));
  };

  return (
    <div className="container my-5">
      <h1 className="text-center mb-4">Gestión de Clientes</h1>

      <h2 className="mb-4">Lista de Clientes</h2>
      {clientes.length > 0 ? (
        <div className="row">
          {clientes.map((cliente) => (
            <div key={cliente._id} className="col-md-4 mb-4">
              <div className="card shadow-lg border-light rounded">
                <div className="card-body">
                  <h5 className="card-title text-primary">{cliente.nombre} {cliente.apellido}</h5>
                  <p className="card-text"><strong>Teléfono:</strong> {cliente.telefono}</p>
                  <button
                    className="btn btn-danger btn-sm mx-1"
                    onClick={() => eliminarCliente(cliente._id)}
                  >
                    Eliminar
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center">No hay clientes disponibles.</p>
      )}

      <AgregarCliente /> {/* Componente para agregar clientes */}
    </div>
  );
};

export default Clientes;
