import React, { useState, useEffect } from 'react';

const AgregarProducto = () => {
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [precio, setPrecio] = useState('');
  const [proveedor, setProveedor] = useState('');
  const [proveedores, setProveedores] = useState([]);

  useEffect(() => {
    // Obtener los proveedores para que el usuario pueda seleccionarlos
    fetch('http://localhost:3003/api/proveedores')
      .then((response) => response.json())
      .then((data) => setProveedores(data))
      .catch((error) => console.error('Error al obtener proveedores:', error));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Llamada a la API para agregar el producto
    fetch('http://localhost:3001/api/productos/agregar', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        nombre,
        descripcion,
        precio,
        proveedor,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.mensaje === 'Producto agregado exitosamente') {
          alert('Producto agregado exitosamente');
          setNombre('');
          setDescripcion('');
          setPrecio('');
          setProveedor('');
        } else {
          alert('Error al agregar el producto');
        }
      })
      .catch((error) => {
        console.error('Error al agregar el producto:', error);
        alert('Error al agregar el producto');
      });
  };

  return (
    <div className="container my-5">
      <h1 className="text-center mb-4">Agregar Producto</h1>
      <form onSubmit={handleSubmit} className="form-group">
        <div className="mb-3">
          <label htmlFor="nombre" className="form-label">Nombre del Producto</label>
          <input
            type="text"
            id="nombre"
            className="form-control"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="descripcion" className="form-label">Descripci√≥n</label>
          <input
            type="text"
            id="descripcion"
            className="form-control"
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="precio" className="form-label">Precio</label>
          <input
            type="number"
            id="precio"
            className="form-control"
            value={precio}
            onChange={(e) => setPrecio(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="proveedor" className="form-label">Proveedor</label>
          <select
            id="proveedor"
            className="form-control"
            value={proveedor}
            onChange={(e) => setProveedor(e.target.value)}
            required
          >
            <option value="">Selecciona un proveedor</option>
            {proveedores.map((prov) => (
              <option key={prov._id} value={prov.nombre}>
                {prov.nombre}
              </option>
            ))}
          </select>
        </div>
        <button type="submit" className="btn btn-primary">Agregar Producto</button>
      </form>
    </div>
  );
};

export default AgregarProducto;
