import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AgregarProveedor from './AgregarProveedor';

const Proveedores = () => {
  const [proveedores, setProveedores] = useState([]); // Aquí se almacenarán los proveedores

  useEffect(() => {
    // Función para obtener los proveedores de la API
    const fetchProveedores = async () => {
      try {
        const response = await axios.get('http://localhost:3003/api/proveedores');
        setProveedores(response.data); // Suponiendo que la respuesta es un arreglo de proveedores
      } catch (error) {
        console.error('Error al obtener los proveedores', error);
      }
    };

    fetchProveedores(); // Llamada a la API cuando el componente se monta
  }, []); // Se ejecuta solo una vez al montarse el componente

  return (
    <div>
      <h1>Gestión de Proveedores</h1>

      <div>
        <h3>Lista de Proveedores</h3>
        {proveedores.length > 0 ? (
          <ul>
            {proveedores.map((proveedor) => (
              <li key={proveedor._id}>
                <h5>{proveedor.nombre}</h5>
                <p><strong>Dirección:</strong> {proveedor.direccion}</p>
                <p><strong>Teléfono:</strong> {proveedor.telefono}</p>
                <p><strong>Email:</strong> {proveedor.email}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No hay proveedores disponibles.</p>
        )}
      </div>

      <div>
        <AgregarProveedor />
      </div>
    </div>
  );
};

export default Proveedores;
