import React, { useState, useEffect } from 'react';
import AgregarProveedor from './AgregarProveedor';

const Proveedores = () => {
  const [proveedores, setProveedores] = useState([]);
  const [editarProveedor, setEditarProveedor] = useState(null);

  useEffect(() => {
    fetch('http://localhost:3003/api/proveedores')
      .then((response) => response.json())
      .then((data) => setProveedores(data))
      .catch((error) => console.error('Error al obtener proveedores:', error));
  }, []);

  const eliminarProveedor = (id) => {
    fetch(`http://localhost:3003/api/proveedores/${id}`, {
      method: 'DELETE',
    })
      .then((response) => {
        if (response.ok) {
          setProveedores((prev) => prev.filter((proveedor) => proveedor._id !== id));
        } else {
          console.error('Error al eliminar proveedor');
        }
      })
      .catch((error) => console.error('Error al eliminar proveedor:', error));
  };

  const actualizarProveedor = (e) => {
    e.preventDefault();
    fetch(`http://localhost:3003/api/proveedores/${editarProveedor._id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(editarProveedor),
    })
      .then((response) => response.json())
      .then((data) => {
        setProveedores((prev) =>
          prev.map((proveedor) =>
            proveedor._id === data._id ? data : proveedor
          )
        );
        setEditarProveedor(null); // Limpiar el estado de ediciÃ³n
      })
      .catch((error) => console.error('Error al actualizar proveedor:', error));
  };

  return (
    <div className="container my-5">
      <h1 className="text-center mb-4">GestiÃ³n de Proveedores</h1>

      <h2 className="mb-4">Lista de Proveedores</h2>
      {proveedores.length > 0 ? (
        <div className="row">
          {proveedores.map((proveedor) => (
            <div key={proveedor._id} className="col-md-4 mb-4">
              <div className="card shadow-lg border-light rounded">
                <div className="card-body">
                  <h5 className="card-title text-primary">{proveedor.nombre}</h5>
                  <p className="card-text"><strong>DirecciÃ³n:</strong> {proveedor.direccion}</p>
                  <p className="card-text"><strong>TelÃ©fono:</strong> {proveedor.telefono}</p>
                  <p className="card-text"><strong>Email:</strong> {proveedor.email}</p>
                  <button
                    className="btn btn-warning btn-sm mx-1"
                    onClick={() => setEditarProveedor(proveedor)}
                  >
                    Editar
                  </button>
                  <button
                    className="btn btn-danger btn-sm mx-1"
                    onClick={() => eliminarProveedor(proveedor._id)}
                  >
                    Eliminar
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center">No hay proveedores disponibles.</p>
      )}

      {editarProveedor && (
        <form onSubmit={actualizarProveedor} className="mt-4">
          <h2>Editar Proveedor</h2>
          <div className="mb-3">
            <label>Nombre</label>
            <input
              type="text"
              className="form-control"
              value={editarProveedor.nombre}
              onChange={(e) =>
                setEditarProveedor({ ...editarProveedor, nombre: e.target.value })
              }
            />
          </div>
          <div className="mb-3">
            <label>DirecciÃ³n</label>
            <input
              type="text"
              className="form-control"
              value={editarProveedor.direccion}
              onChange={(e) =>
                setEditarProveedor({ ...editarProveedor, direccion: e.target.value })
              }
            />
          </div>
          <div className="mb-3">
            <label>TelÃ©fono</label>
            <input
              type="text"
              className="form-control"
              value={editarProveedor.telefono}
              onChange={(e) =>
                setEditarProveedor({ ...editarProveedor, telefono: e.target.value })
              }
            />
          </div>
          <div className="mb-3">
            <label>Email</label>
            <input
              type="email"
              className="form-control"
              value={editarProveedor.email}
              onChange={(e) =>
                setEditarProveedor({ ...editarProveedor, email: e.target.value })
              }
            />
          </div>
          <button type="submit" className="btn btn-success">Guardar</button>
          <button
            type="button"
            className="btn btn-secondary mx-2"
            onClick={() => setEditarProveedor(null)}
          >
            Cancelar
          </button>
        </form>
      )}

      <AgregarProveedor />
    </div>
  );
};

export default Proveedores;
