import React, { useState } from 'react';

const AgregarEmpleado = () => {
  const [formData, setFormData] = useState({
    nombre: '',
    area: '',
    sueldo: '',
    sucursal: '',
  });

  const [mensaje, setMensaje] = useState(''); // Mensaje para mostrar al usuario

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:3001/api/empleados/agregar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      if (response.ok) {
        setMensaje('Empleado agregado con éxito');
        setFormData({ nombre: '', area: '', sueldo: '', sucursal: '' }); // Limpiar el formulario
      } else {
        setMensaje(`Error al agregar el empleado: ${data.mensaje}`);
      }
    } catch (error) {
      console.error('Error:', error);
      setMensaje('Hubo un error al intentar agregar el empleado');
    }
  };

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4">Agregar Empleado</h2>

      {/* Formulario */}
      <form onSubmit={handleSubmit} className="form-group shadow-lg p-4 rounded bg-light">
        <div className="mb-3">
          <label htmlFor="nombre" className="form-label">Nombre del Empleado</label>
          <input
            type="text"
            name="nombre"
            className="form-control"
            value={formData.nombre}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label htmlFor="area" className="form-label">Área</label>
          <input
            type="text"
            name="area"
            className="form-control"
            value={formData.area}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label htmlFor="sueldo" className="form-label">Sueldo</label>
          <input
            type="number"
            name="sueldo"
            className="form-control"
            value={formData.sueldo}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label htmlFor="sucursal" className="form-label">Sucursal</label>
          <input
            type="text"
            name="sucursal"
            className="form-control"
            value={formData.sucursal}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" className="btn btn-primary btn-block">Agregar Empleado</button>
      </form>

      {/* Mensaje de éxito o error */}
      {mensaje && <p className="mt-4 text-center">{mensaje}</p>}
    </div>
  );
};

export default AgregarEmpleado;
