import React, { useState, useEffect } from 'react';

const Proveedores = () => {
  const [proveedores, setProveedores] = useState([]);  // Inicializar con un arreglo vacío
  const [error, setError] = useState(null);

  useEffect(() => {
    // Realizar la solicitud a la API
    fetch('http://localhost:3003/api/proveedores')
      .then((response) => {
        // Verifica el tipo de respuesta
        console.log('Respuesta de la API:', response);

        if (!response.ok) {
          throw new Error('Error en la solicitud');
        }
        return response.json();  // Convierte la respuesta a JSON
      })
      .then((data) => {
        // Verifica los datos antes de actualizar el estado
        console.log('Datos obtenidos:', data);

        // Verifica que los datos sean un arreglo
        if (Array.isArray(data)) {
          setProveedores(data);  // Actualiza el estado con los datos de los proveedores
        } else {
          setError('La respuesta de la API no es un arreglo');
        }
      })
      .catch((err) => {
        // Manejar cualquier error
        setError(err.message);
      });
  }, []);

  if (error) {
    return <div>Error al obtener los proveedores: {error}</div>;
  }

  return (
    <div>
      <h1>Gestión de Proveedores</h1>
      <button>Editar</button>
      <button>Eliminar</button>
      <h2>Lista de Proveedores</h2>
      {proveedores.length === 0 ? (
        <p>No hay proveedores disponibles.</p>
      ) : (
        <ul>
          {proveedores.map((proveedor) => (
            <li key={proveedor._id}>
              <strong>{proveedor.nombre}</strong><br />
              Dirección: {proveedor.direccion}<br />
              Teléfono: {proveedor.telefono}<br />
              Email: {proveedor.email}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Proveedores;
