import React, { useEffect, useState } from 'react';

const Clientes = () => {
  const [clientes, setClientes] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchClientes = async () => {
      try {
        const response = await fetch('http://localhost:3006/api/clientes');
        if (!response.ok) {
          throw new Error('Error al obtener clientes');
        }
        const data = await response.json();
        setClientes(data);
      } catch (error) {
        console.error('Error al obtener clientes:', error);
        setError('No se pudo cargar la lista de clientes.');
      }
    };

    fetchClientes();
  }, []);

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div className="container my-5">
      <h1 className="text-center mb-4">Gestión de Clientes</h1>
      <h2 className="mb-4">Lista de Clientes</h2>
      {clientes.length > 0 ? (
        <div className="row">
          {clientes.map((cliente) => (
            <div key={cliente._id} className="col-md-4 mb-4">
              <div className="card shadow-lg border-light rounded">
                <div className="card-body">
                  <h5 className="card-title text-primary">
                    {cliente.nombre} {cliente.apellido}
                  </h5>
                  <p className="card-text">
                    <strong>Teléfono:</strong> {cliente.telefono}
                  </p>
                  <p className="card-text">
                    <strong>Empleado Suscriptor:</strong>{' '}
                    {cliente.empleado ? cliente.empleado.nombre : 'Sin asignar'}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center">No hay clientes disponibles.</p>
      )}
    </div>
  );
};

export default Clientes;
