import React, { useState, useEffect } from 'react';

const AgregarProducto = () => {
  const [formData, setFormData] = useState({
    nombre: '',
    descripcion: '',
    precio: '',
    proveedor: '',
  });

  const [mensaje, setMensaje] = useState(''); // Mensaje para mostrar al usuario
  const [proveedores, setProveedores] = useState([]); // Lista de proveedores

  // Obtener proveedores al cargar el componente
  useEffect(() => {
    fetch('http://localhost:3003/api/proveedores') // Asegúrate de que esta sea la URL correcta
      .then((response) => response.json())
      .then((data) => setProveedores(data))
      .catch((error) => console.error('Error al obtener proveedores:', error));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:3001/api/productos/agregar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setMensaje('Empleado agregado exitosamente');
        // Limpiar los campos del formulario
        setNombre('');
        setArea('');
        setSueldo('');
        setSucursal('');
      } else {
        setMensaje(`Error: ${data.mensaje}`);
      }
      } catch (error) {
        setMensaje('Error al agregar el empleado');
        console.error('Error:', error);
      }

  };

  return (
    <div className="container my-5">
      <h2 className="text-center mb-4">Agregar Producto</h2>

      {/* Formulario */}
      <form onSubmit={handleSubmit} className="form-group shadow-lg p-4 rounded bg-light">
        <div className="mb-3">
          <label htmlFor="nombre" className="form-label">Nombre del Producto</label>
          <input
            type="text"
            name="nombre"
            className="form-control"
            value={formData.nombre}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label htmlFor="descripcion" className="form-label">Descripción</label>
          <input
            type="text"
            name="descripcion"
            className="form-control"
            value={formData.descripcion}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label htmlFor="precio" className="form-label">Precio</label>
          <input
            type="number"
            name="precio"
            className="form-control"
            value={formData.precio}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label htmlFor="proveedor" className="form-label">Proveedor</label>
          <select
            name="proveedor"
            className="form-control"
            value={formData.proveedor}
            onChange={handleChange}
            required
          >
            <option value="">Seleccione un proveedor</option>
            {proveedores.map((proveedor) => (
              <option key={proveedor._id} value={proveedor._id}>
                {proveedor.nombre}
              </option>
            ))}
          </select>
        </div>

        <button type="submit" className="btn btn-primary btn-block">Agregar Producto</button>
      </form>

      {/* Mensaje de éxito o error */}
      {mensaje && <p className="mt-4 text-center">{mensaje}</p>}
    </div>
  );
};

export default AgregarProducto;
