import React, { useState, useEffect } from 'react';

const AgregarProducto = () => {
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [precio, setPrecio] = useState('');
  const [proveedor, setProveedor] = useState('');
  const [proveedores, setProveedores] = useState([]);  // Estado para los proveedores

  // Cargar la lista de proveedores desde la API
  useEffect(() => {
    fetch('http://localhost:3003/api/proveedores') // Ruta para obtener proveedores
      .then((response) => response.json())
      .then((data) => setProveedores(data))
      .catch((error) => console.error('Error al obtener proveedores:', error));
  }, []);

  // Manejo del envío del formulario para agregar un producto
  const handleSubmit = (e) => {
    e.preventDefault();

    // Validar los campos antes de enviar
    if (!nombre || !descripcion || !precio || !proveedor) {
      alert('Todos los campos son obligatorios');
      return;
    }

    const nuevoProducto = { nombre, descripcion, precio, proveedor };

    fetch('http://localhost:3001/api/productos/agregar', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(nuevoProducto),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.mensaje === 'Producto agregado exitosamente') {
          alert('Producto agregado con éxito');
          // Limpiar formulario después de agregar el producto
          setNombre('');
          setDescripcion('');
          setPrecio('');
          setProveedor('');
        } else {
          alert('Error al agregar el producto');
        }
      })
      .catch((error) => console.error('Error al agregar el producto:', error));
  };

  return (
    <div className="container my-5">
      <h2 className="mb-4">Agregar Producto</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nombre del Producto</label>
          <input
            type="text"
            className="form-control"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Descripción</label>
          <input
            type="text"
            className="form-control"
            value={descripcion}
            onChange={(e) => setDescripcion(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Precio</label>
          <input
            type="number"
            className="form-control"
            value={precio}
            onChange={(e) => setPrecio(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Proveedor</label>
          <select
            className="form-control"
            value={proveedor}
            onChange={(e) => setProveedor(e.target.value)}
            required
          >
            <option value="">Seleccione un proveedor</option>
            {proveedores.map((proveedor) => (
              <option key={proveedor._id} value={proveedor._id}>
                {proveedor.nombre}
              </option>
            ))}
          </select>
        </div>

        <button type="submit" className="btn btn-primary mt-3">
          Agregar Producto
        </button>
      </form>
    </div>
  );
};

export default AgregarProducto;
