// src/components/AgregarProducto.js
import React, { useState } from 'react';

const AgregarProducto = () => {
  const [formData, setFormData] = useState({
    nombre: '',
    descripcion: '',
    precio: '',
    proveedor: '',
  });

  const [mensaje, setMensaje] = useState(''); // Mensaje para mostrar al usuario

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:3001/productos/agregar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      if (response.ok) {
        setMensaje('Producto agregado con éxito');
        setFormData({ nombre: '', descripcion: '', precio: '', proveedor: '' }); // Limpiar el formulario
      } else {
        setMensaje(`Error al agregar el producto: ${data.mensaje}`);
      }
    } catch (error) {
      console.error('Error:', error);
      setMensaje('Hubo un error al intentar agregar el producto');
    }
  };

  return (
    <div>
      <h2>Agregar Producto</h2>
      <form onSubmit={handleSubmit} style={{ marginTop: '20px' }}>
        <div>
          <label>Nombre del Producto:</label>
          <input
            type="text"
            name="nombre"
            value={formData.nombre}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Descripción:</label>
          <input
            type="text"
            name="descripcion"
            value={formData.descripcion}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Precio:</label>
          <input
            type="number"
            name="precio"
            value={formData.precio}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Proveedor:</label>
          <input
            type="text"
            name="proveedor"
            value={formData.proveedor}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Agregar Producto</button>
      </form>

      {mensaje && <p>{mensaje}</p>} {/* Mostrar mensaje de éxito o error */}
    </div>
  );
};

export default AgregarProducto;
