// src/components/Empleados.js
import React, { useState, useEffect } from 'react';

const Empleados = () => {
  const [empleados, setEmpleados] = useState([]); // Estado para almacenar los empleados

  // Obtener empleados desde la API al cargar el componente
  useEffect(() => {
    fetch('http://localhost:3005/api/empleados') // Asegúrate de que esta sea la URL correcta
      .then((response) => {
        if (!response.ok) {
          throw new Error('Error en la respuesta de la API');
        }
        return response.json();
      })
      .then((data) => {
        console.log('Datos recibidos de la API:', data); // Verifica los datos en la consola
        if (Array.isArray(data)) {
          setEmpleados(data); // Actualiza el estado solo si es un array
        } else {
          console.error('La respuesta no es un array:', data);
        }
      })
      .catch((error) => console.error('Error al obtener empleados:', error));
  }, []);

  return (
    <div className="container my-5">
      <h1 className="text-center mb-4">Gestión de Empleados</h1>

      <h2 className="mb-4">Lista de Empleados</h2>
      {empleados.length > 0 ? ( // Comprueba si hay empleados para mostrar
        <div className="row">
          {empleados.map((empleado) => (
            <div key={empleado._id} className="col-md-4 mb-4">
              <div className="card shadow-lg border-light rounded">
                <div className="card-body">
                  <h5 className="card-title text-primary">{empleado.nombre}</h5>
                  <p className="card-text"><strong>Área:</strong> {empleado.area}</p>
                  <p className="card-text"><strong>Sueldo:</strong> ${empleado.sueldo}</p>
                  <p className="card-text"><strong>Sucursal:</strong> {empleado.sucursal}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center">No hay empleados disponibles.</p> // Mensaje si no hay empleados
      )}
    </div>
  );
};

export default Empleados;
