import React, { useState, useEffect } from 'react';

const Proveedores = () => {
  const [proveedores, setProveedores] = useState([]);  // Inicializar con un arreglo vacío
  const [error, setError] = useState(null);

  useEffect(() => {
    // Realizar la solicitud a la API
    fetch('http://localhost:3003/api/proveedores')
      .then((response) => {
        console.log('Respuesta de la API:', response);  // Verifica el contenido de la respuesta
        if (!response.ok) {
          throw new Error('No se pudo obtener la información');
        }

        // Intentamos leer el cuerpo como texto
        return response.text(); // Leer como texto primero
      })
      .then((text) => {
        console.log('Texto recibido:', text);  // Verifica si la respuesta es HTML o algo más
        try {
          // Intentar convertir el texto en JSON si es posible
          const data = JSON.parse(text);
          setProveedores(data);
        } catch (error) {
          // Si no es un JSON válido, mostrar el texto como error
          setError('La respuesta no es un JSON válido');
        }
      })
      .catch((err) => {
        setError(err.message);
      });
  }, []);  // El segundo parámetro [] asegura que esto se ejecute solo una vez al montar el componente


  if (error) {
    return <div>Error al obtener los proveedores: {error}</div>;
  }

  return (
    <div>
      <h1>Gestión de Proveedores</h1>
      <button>Editar</button>
      <button>Eliminar</button>
      <h2>Lista de Proveedores</h2>
      {proveedores.length === 0 ? (
        <p>No hay proveedores disponibles.</p>
      ) : (
        <ul>
          {proveedores.map((proveedor) => (
            <li key={proveedor._id}>
              <strong>{proveedor.nombre}</strong><br />
              Dirección: {proveedor.direccion}<br />
              Teléfono: {proveedor.telefono}<br />
              Email: {proveedor.email}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Proveedores;
